import "reflect-metadata";
import { FunctionMetadata } from "../types/metadata";
/**
 * Defines the structure for a property within a Daemo schema.
 */
export interface SchemaProperty {
    type: "string" | "number" | "boolean" | "object" | "array" | "any";
    description: string;
    required?: boolean;
    format?: "date-time";
    items?: SchemaProperty;
    properties?: {
        [key: string]: SchemaProperty;
    };
}
/**
 * The developer-friendly schema definition object passed to the @DaemoSchema decorator.
 */
export interface SchemaDefinition {
    description?: string;
    properties: {
        [key: string]: SchemaProperty;
    };
}
/**
 * Decorator to attach a detailed, explicit schema to a class.
 * This schema is used for generating the function interface for the AI agent.
 * @param schema The detailed definition of the class properties.
 */
export declare function DaemoSchema(schema: SchemaDefinition): ClassDecorator;
/**
 * Retrieves the schema definition attached to a class by the @DaemoSchema decorator.
 */
export declare function getDaemoSchema(target: any): SchemaDefinition | undefined;
/**
 * Decorator to mark a method as a Daemo function
 * @param metadata Function metadata including description, examples, tags, etc.
 */
export declare function DaemoFunction(metadata?: FunctionMetadata): MethodDecorator;
/**
 * Decorator to add description to a parameter
 * @param description Parameter description
 */
export declare function ParameterDescription(description: string): ParameterDecorator;
/**
 * Decorator to mark a parameter for context injection
 * @param key The context key to inject
 */
export declare function FromDaemoContext(key: string): ParameterDecorator;
/**
 * Get function metadata from a method
 */
export declare function getDaemoFunctionMetadata(target: any, propertyKey: string): FunctionMetadata | undefined;
/**
 * Get parameter descriptions from a method
 */
export declare function getParameterDescriptions(target: any, propertyKey: string): Map<number, string>;
/**
 * Check if a method has the DaemoFunction decorator
 */
export declare function hasDaemoFunction(target: any, propertyKey: string): boolean;
/**
 * Get all methods with DaemoFunction decorator from a class
 */
export declare function getDaemoFunctions(target: any): string[];
