/**
 * Configuration types for Daemo SDK
 */
import { QueryStreamEvent } from "./results";
export interface DaemoHostedOptions {
    daemoGatewayUrl?: string;
    agentApiKey?: string;
    mongodb?: MongoDbConfig;
    localFile?: LocalFileConfig;
}
export interface StreamCallbacks {
    onData: (data: QueryStreamEvent) => void;
    onError: (error: Error) => void;
    onEnd: () => void;
}
export interface DaemoClientOptions {
    daemoAgentUrl?: string;
    agentApiKey?: string;
}
export interface LlmConfig {
    provider: string;
    apiKey?: string;
    endpoint?: string;
    model?: string;
    maxTokens?: number;
}
export interface MongoDbConfig {
    connectionString: string;
    databaseName: string;
}
export interface LocalFileConfig {
    basePath?: string;
}
export interface StorageConfig {
    mongodb?: MongoDbConfig;
    localFile?: LocalFileConfig;
}
