/**
 * Result types for Daemo operations
 */
export interface ToolCallInfo {
    toolName: string;
    parametersJson: string;
    resultJson?: string;
    success: boolean;
    errorMessage?: string;
    executionTimeMs: number;
}
export interface QueryResult {
    success: boolean;
    response: string;
    errorMessage?: string;
    threadId?: string;
    toolInteractions: ToolCallInfo[];
    executionTimeMs: number;
}
export declare enum QueryStreamEventType {
    Thought = "thought",
    ToolCall = "tool_call",
    ToolResult = "tool_result",
    PartialResponseChunk = "partial_response_chunk",
    FinalResponse = "final_response",
    Error = "error"
}
export interface QueryStreamEvent {
    type: QueryStreamEventType;
    data: string | ToolCallInfo | QueryResult | {
        toolCall: ToolCallInfo;
        toolResult: ToolCallInfo;
    };
}
export interface ThreadInfo {
    threadId: string;
    createdAt: Date;
    updatedAt: Date;
    messageCount: number;
    dataCount: number;
    memorySizeBytes: number;
    sessionId?: string;
    lastUserMessage?: string;
}
export interface ThreadMessage {
    id: string;
    timestamp: Date;
    role: string;
    content: string;
}
export interface CreateThreadResult {
    success: boolean;
    threadId?: string;
    errorMessage?: string;
}
export interface ListThreadsResult {
    success: boolean;
    threads: ThreadInfo[];
    errorMessage?: string;
}
export interface GetThreadResult {
    success: boolean;
    thread?: ThreadInfo;
    recentMessages: ThreadMessage[];
    errorMessage?: string;
}
export interface DeleteThreadResult {
    success: boolean;
    errorMessage?: string;
}
