/**
 * Utility functions for TypeScript type extraction and conversion
 */
/**
 * Convert a JavaScript/TypeScript type to a string representation
 */
export declare function getTypeString(value: any): string;
/**
 * Extract parameter types from a function using TypeScript metadata
 */
export declare function getParameterTypes(target: any, propertyKey: string): any[];
/**
 * Extract return type from a function using TypeScript metadata
 */
export declare function getReturnType(target: any, propertyKey: string): any;
/**
 * Get TypeScript type name from constructor
 */
export declare function getTypeName(type: any): string;
/**
 * Check if a function is async
 */
export declare function isAsyncFunction(fn: Function): boolean;
/**
 * Extract the actual return type from async functions
 */
export declare function getActualReturnType(returnType: any): string;
/**
 * Convert camelCase to PascalCase
 */
export declare function toPascalCase(str: string): string;
/**
 * Convert PascalCase to camelCase
 */
export declare function toCamelCase(str: string): string;
/**
 * Check if a string is a valid TypeScript identifier
 */
export declare function isValidTypeScriptIdentifier(name: string): boolean;
